const client = require('../index');
const chatModel = require('../models/chatGSchema')
const config = require('../config.json');

async function Punish(message, GuardType, Embed) {
    let result = false;
    const Database = await chatModel.findOne({ ServerID: message.guild.id });
    const LogChannel = message.guild.channels.cache.find(channel => channel.id === config.ChatGuard.logchannel);
    let TimeoutDuration = Database.MuteDurationMinute;

    if(Date.now() < message.member.communicationDisabledUntilTimestamp) return;
    if(Database.MuteDurationMinute < 1)TimeoutDuration = 60;
    const CanBeMuted = message.guild.me.permissions.has('MODERATE_MEMBERS');
    const CanBeLog = LogChannel && LogChannel.type === 'GUILD_TEXT';

    if (!CanBeMuted) return console.log('WARN - The operation failed.  Reason: I don\'t have "MODERATE_MEMBERS" permission');
    if(!CanBeLog) return console.log('WARN - The operation failed.  Reason: Searched logchannel was not found');
    if(Database.BlueListMembers.includes(message.member.id) === true)result = true;
    if(result === true) {

        await chatModel.findOneAndUpdate({ ServerID: message.guild.id }, { $pull: { BlueListMembers: message.member.id }});
        await chatModel.findOneAndUpdate({ ServerID: message.guild.id }, { $push: { BlackListMembers: message.member.id } }, { upsert: true });
        
        message.member.timeout(TimeoutDuration * 1000 * 60, 'chatGuard');
     
        setTimeout(async() => { 
            await chatModel.findOneAndUpdate({ ServerID: message.guild.id }, { $pull: { BlackListMembers: message.member.id } });
            if(message.member.isCommunicationDisabled()) message.member.timeout(null, 'chatGuard').catch(() => {}); }, TimeoutDuration * 1000 * 60 ); 

        if(GuardType === 'CharacterLimit') {
            message.channel.send({ embeds: [Embed.setDescription('<@'+message.author.id+'>, Aşırı karakter kullandığın için **'+TimeoutDuration+'** Dakika Mute yedin. Lütfen kurallara uymaya özen göster')]}).catch(() => {});
            return LogChannel.send({ embeds: [Embed.setDescription('<@'+message.author.id+'>, Nickli kullanıcı '+message.content.length+' uzunluğunda mesaj yazdığı için **'+TimeoutDuration+'** Dakika boyunca mutelendi.')]}).catch(() => {});
        }
        if(GuardType === 'MassPingGuard') {
            message.channel.send({ embeds: [Embed.setDescription('<@'+message.author.id+'>, Aşırı etiket attığın için **'+TimeoutDuration+'** Dakika Mute yedin. Lütfen kurallara uymaya özen göster!')]}).catch(() => {});
            return LogChannel.send({ embeds: [Embed.setDescription('<@'+message.author.id+'>, Nickli kullanıcı mesajında **'+message.mentions.users.size+'** kişiyi etiketlediği için **'+TimeoutDuration+'** Dakika boyunca mutelendi.')]}).catch(() => {});
        }
        if(GuardType === 'InviteGuard') {
            message.channel.send({ embeds: [Embed.setDescription('<@'+message.author.id+'>, Sunucu daveti attığın için **'+TimeoutDuration+'** Dakika Mute yedin. Lütfen kurallara uymaya özen göster')]}).catch(() => {});
            return LogChannel.send({ embeds: [Embed.setDescription('<@'+message.author.id+'>, Nickli kullanıcı **'+message.content+'** içerikli mesajında sunucu daveti bulunduğu için **'+TimeoutDuration+'** Dakika boyunca mutelendi.')]}).catch(() => {});
        }
        if(GuardType === 'SpamGuard') {
            message.channel.send({ embeds: [Embed.setDescription('<@'+message.author.id+'>, Spam yaptığın için **'+TimeoutDuration+'** Dakika Mute yedin. Lütfen kurallara uymaya özen göster')]}).catch(() => {});
            return LogChannel.send({ embeds: [Embed.setDescription('<@'+message.author.id+'>, Nickli kullanıcı Spam yaptığı için **'+TimeoutDuration+'** Dakika boyunca mutelendi.')]}).catch(() => {});
        }
    }
    if(result === false) {

        await chatModel.findOneAndUpdate({ ServerID: message.guild.id }, { $push: { BlueListMembers: message.member.id } }, { upsert: true });
        setTimeout(async() => {
            await chatModel.findOneAndUpdate({ ServerID: message.guild.id }, { $pull: { BlueListMembers: message.member.id } }); 
        }, 7200000); 

        if(GuardType === 'CharacterLimit') {
            message.channel.send({ embeds: [Embed.setDescription('<@'+message.author.id+'>, You\'re not allowed to use extreme characters, so you\'ll be punished if you repeat it !')]}).catch(() => {});
            return LogChannel.send({ embeds: [Embed.setDescription('<@'+message.author.id+'>, named user has received a warning because he wrote **'+message.content.length+'** chars like what OwO.')]}).catch(() => {});
        }
        if(GuardType === 'MassPingGuard') {
            message.channel.send({ embeds: [Embed.setDescription('<@'+message.author.id+'>, You\'re not allowed to massping, you\'ll be fined if you repeat it !')]}).catch(() => {});
            return LogChannel.send({ embeds: [Embed.setDescription('<@'+message.author.id+'>, named user has received a warning because he mentioned **'+message.mentions.users.size+'** users.')]}).catch(() => {});
        }
        if(GuardType === 'InviteGuard') {
            message.channel.send({ embeds: [Embed.setDescription('<@'+message.author.id+'>, You are not allowed to send a Server Invitation; if it repetitions you will be punished !')]}).catch(() => {});
            return LogChannel.send({ embeds: [Embed.setDescription('<@'+message.author.id+'>, named user has received a warning because he tried to include a server invitation in the message like **'+message.content+'**.')]}).catch(() => {});
        }
        if(GuardType === 'SpamGuard') {
            message.channel.send({ embeds: [Embed.setDescription('<@'+message.author.id+'>, You are not allowed to spam; if you do so, you will be punished !')]}).catch(() => {});
            return LogChannel.send({ embeds: [Embed.setDescription('<@'+message.author.id+'>,  named user has received a warning because he spammed.')]}).catch(() => {});
        }
    }
    return result;
}
  
async function WhiteList(message) {
    let result = false;
    const Database = await chatModel.findOne({ ServerID: message.guild.id });
    if (Database && Database.WhiteListMembers.includes(message.member.id) === true)result = true;
    if(Database && Database.WhiteListChannels.includes(message.channel.id) === true)result = true;
    let roles = message.member.roles.cache.map(role => role.id);
    if (roles.some(role => Database.WhiteListRoles.includes(role)))result = true;
    return result;
}

