client.cache = {
    messages: []
};

async function Spam(message) {
    let result = false;
    const currentmsg = {
        messageID: message.id,
        guildID: message.guild.id,
        authorID: message.author.id,
        channelID: message.channel.id,
        sentTimestamp: message.createdTimestamp
    };
    client.cache.messages.push(currentmsg);

    const spammessage = client.cache.messages.filter((db) => db.authorID === message.author.id && db.guildID === message.guild.id);
    const spamamount = spammessage.filter((db) => db.sentTimestamp > (Date.now() - 2000));
    if(spamamount.length >= 4){await reset(); return result = true; }
    return result;
}

function reset () {
    client.cache = {
        messages: []
    };}


